mapper.select:
   SELECT * FROM `storagedatacollection`
    WHERE 1 =1

	  and  `apid`  = #{apid} 


	    AND `deviceid` = #{deviceid} 


	    AND `key` in(${key}) 

    AND `addtime` > #{startTime} AND time < #{endTime}  ORDER BY `addtime` desc


   SELECT * FROM `apstatus` where `apid`=#{apid} 

	    AND `key` in(${key}) 

    ORDER BY id desc LIMIT 0,20 ;


   SELECT * FROM `devstatus` where  1=1

	  and  `apid`  = #{apid} 


	    AND `deviceid` = #{deviceid} 

     AND `key` in(${key})   
   ORDER BY id desc LIMIT 0,20 ;
