mapper.select:
		select * from ${table}
		where `key`=#{key} AND oid=#{oid} AND
		`addtime` >= #{startTime} AND `addtime`  <  #{endTime}
        order by `addtime` desc

		select `value`,`addtime` from ${table}
		where `key`=#{key} AND oid=#{oid} AND
		`addtime` >= #{startTime} AND `addtime`  <  #{endTime}
        order by `addtime` ${orderBy}

		select * from ${table}
		where `key`=#{key} and oid=#{oid}
        order by `addtime` desc
        limit #{nums}
