mapper.select:
    SELECT * FROM  warningsinfo   WHERE `rdcid` =#{rdcId}  
     and DATE_FORMAT( `addtime`, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )  
     and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format( `addtime`, '%Y%m' ) ) =#{month}  
	 GROUP BY `warningname` ;

	SELECT
	 @sm,@tp,(@sm := @sm +IF (TIMESTAMPDIFF(SECOND ,@tp, w.`addtime`) >#{jtime},1,0)) AS id,
	  @tp := w.`addtime` as addtime,
	 `warningname` 
	FROM
		`warningsinfo` w
	WHERE `rdcid` =#{rdcid}
	      and `warningname` like '%#{wartype}%'
        and w.addtime BETWEEN  #{stTime} and #{enTime}
	ORDER BY
		id DESC
	LIMIT 1;
