mapper.select:
SELECT r.id, r.name, r.address, r.addtime, t.rdcscore score, t.rdccommentcnt userCommentCount, t.rdcrecommendpercent userRecommendPercent, t.pageview, ( SELECT location FROM filedata f WHERE f.belongid = r.id AND f.category = 'storagePic' ORDER BY id DESC LIMIT 1) AS logo FROM Rdc r LEFT JOIN rdcext t ON t.RDCID = r.id WHERE 1=1 and r.userid = ${userID}
AND r.name like "%"#{keyword}"%"
ORDER BY r.addTime DESC
