mapper.select:
select  r.sqm rdcSqm,  r.id rdcID, t.managetype codeLave1, r.name,r.provinceid,r.cityid, r.address, r.audit from rdc r LEFT JOIN rdcext t on t.RDCID=r.id where 1=1

    and r.userid=#{userid}  
     and r.audit=#{audit}    


  and r.id=#{rdcId}

  GROUP BY r.id ORDER BY r.addtime  desc

SELECT s.id, s.rdcID, s.releaseID, s.title, s.typeCode, s.typeText, s.unitPrice, s.unit, s.sqm, s.detlAddress, st.type codeLave2, date_format(s.validStartTime,'%Y-%m-%d') validStartTime, date_format(s.validEndTime,'%Y-%m-%d') validEndTime, r.address, r.name, r.coldtype, (SELECT location FROM filedata f WHERE f.belongid = s.id AND f.category ='sharePic' ORDER BY id DESC LIMIT 1 ) as logo FROM shared_info s LEFT JOIN rdc r ON s.rdcID = r.id LEFT JOIN rdcext t on s.rdcID=t.RDCID LEFT JOIN storagetempertype st on st.id=s.codeLave2 WHERE s.stauts = #{sstauts} and s.dataType=#{datatype} and s.rdcID=#{rdcID}
and s.typeCode=#{type} and ${sqm} and s.title like '%${keyword}%' and s.provinceid = #{provinceid} and s.codeLave1 IN (${managetype})
and s.codeLave2 in (${storagetempertype}) GROUP BY s.id ORDER BY s.updatetime DESC

SELECT s.id, s.rdcID, s.releaseID, s.title, s.typeCode, s.typeText, s.unitPrice, s.telephone, s.unit, s.unit1, s.unit2, s.sqm, s.detlAddress, st.type codeLave2, date_format(s.validStartTime,'%Y-%m-%d') validStartTime, date_format(s.validEndTime,'%Y-%m-%d') validEndTime, r.audit, r.istemperaturestandard, r.address, r.name, r.coldtype, (SELECT location FROM filedata f WHERE f.belongid = s.id AND f.category ='sharePic' ORDER BY id DESC LIMIT 1 ) as logo FROM shared_info s LEFT JOIN rdc r ON s.rdcID = r.id LEFT JOIN rdcext t on s.rdcID=t.RDCID LEFT JOIN storagetempertype st on st.id=s.codeLave2
s.dataType=#{dataType}
and s.typeCode=#{typeCode}
GROUP BY s.id ORDER BY s.updatetime DESC limit 0,14

SELECT s.id, s.rdcID, s.releaseID, s.title, s.typeCode, s.dataType, s.typeText, s.unitPrice, s.telephone, s.unit, s.unit1, s.unit2, s.sqm, s.detlAddress, s.rentdate, s.username, s.publishunit, s.codeLave1, s.codeLave2, s.codeLave4, validStartTime, validEndTime, r.audit, r.istemperaturestandard, r.address, r.name, r.coldtype, r.sqm rdcSqm, r.infoIntegrity, cl.CityName, pl.ProvinceName, (SELECT location FROM filedata f WHERE f.belongid = s.id AND f.category ='sharePic' ORDER BY id DESC LIMIT 1 ) as logo FROM shared_info s LEFT JOIN `rdc` r ON r.`id`=s.`rdcID` LEFT JOIN `citylist` cl ON cl.`CityId`=s.`cityid` LEFT JOIN `provincelist` pl ON pl.`ProvinceId`=s.`provinceid`
s.dataType=#{dataType} and s.typeCode=#{typeCode} and ${sqm} and s.title like '%${keyword}%' and s.provinceid=#{provinceid} and s.codeLave1 IN (${managetype}) and s.codeLave2 in (${storagetempertype}) and s.codeLave1 in (${goodtype}) and rdcID=#{rdcID}
GROUP BY s.id ORDER BY s.updatetime DESC

mapper.insert:
SELECT LAST_INSERT_ID() AS id
INSERT INTO shared_info
rdcID =#{rdcID } , releaseID =#{releaseID } , rentdate =#{rentdate } , username =#{username } , publishunit =#{publishunit } , codeLave1 =#{codeLave1 } , codeLave2 =#{codeLave2 } , codeLave3 =#{codeLave3 } , codeLave4 =#{codeLave4 } , title =#{title } , typeCode =#{typeCode } , typeText =#{typeText } , unitPrice =#{unitPrice } , sqm =#{sqm } , unit =#{unit } , unit1 =#{unit1 } , unit2 =#{unit2 } , validStartTime =#{validStartTime } , validEndTime =#{validEndTime } , applyID =#{applyID } , bookings =#{bookings } , attrvalue =#{attrvalue } , attrvalue1 =#{attrvalue1 } , attrvalue2 =#{attrvalue2 } , telephone =#{telephone } , note =#{note } , stauts =#{stauts } , stprovinceID =#{stprovinceID } , stcityID =#{stcityID } , staddress =#{staddress } , toprovinceID =#{toprovinceID } , tocityID =#{tocityID } , toaddress =#{toaddress } , provinceid =#{provinceid } , cityid =#{cityid } , address =#{address } , detlAddress =#{detlAddress } , dataType =#{dataType }

mapper.update:
update shared_info
rdcID =#{rdcID } , releaseID =#{releaseID } , codeLave1 =#{codeLave1 } , codeLave2 =#{codeLave2 } , codeLave3 =#{codeLave3 } , codeLave4 =#{codeLave4 } , rentdate =#{rentdate } , username =#{username } , publishunit =#{publishunit} , title =#{title } , typeCode =#{typeCode } , typeText =#{typeText } , unitPrice =#{unitPrice } , sqm =#{sqm } , unit =#{unit } , unit1 =#{unit1 } , unit2 =#{unit2 } , validStartTime =#{validStartTime } , validEndTime =#{validEndTime } , applyID =#{applyID } , bookings =#{bookings } , attrvalue =#{attrvalue } , attrvalue1 =#{attrvalue1 } , attrvalue2 =#{attrvalue2 } , telephone =#{telephone } , note =#{note } , stauts =#{stauts } , stprovinceID =#{stprovinceID } , stcityID =#{stcityID } , staddress =#{staddress } , toprovinceID =#{toprovinceID } , tocityID =#{tocityID } , toaddress =#{toaddress } , provinceid =#{provinceid } , cityid =#{cityid } , address =#{address } , detlAddress =#{detlAddress } , dataType =#{dataType }
where id = #{id};
