mapper.select:
SELECT s.id, s.rdcID, s.releaseID, s.title, s.typeCode, s.typeText, s.unitPrice, s.unit, s.sqm, s.detlAddress, st.type codeLave2, date_format(s.validStartTime,'%Y-%m-%d') validStartTime, date_format(s.validEndTime,'%Y-%m-%d') validEndTime, r.address, r.name, r.coldtype, (SELECT location FROM filedata f WHERE f.belongid = s.id AND f.category ='sharePic' ORDER BY id DESC LIMIT 1 ) as logo FROM shared_info s LEFT JOIN rdc r ON s.rdcID = r.id LEFT JOIN rdcext t on s.rdcID=t.RDCID LEFT JOIN storagetempertype st on st.id=s.codeLave2 WHERE s.stauts = #{sstauts} and s.dataType=#{datatype} and s.rdcID=#{rdcID}
and t.managetype in (${managetype}) and s.typeCode=#{type} and ${sqm} and s.title like '%${keyword}%' and s.provinceid = #{provinceid} and t.managetype in (${managetype})
and s.codeLave2 in (${storagetempertype}) GROUP BY s.id ORDER BY s.updatetime DESC

mapper.insert:
SELECT LAST_INSERT_ID() AS id
INSERT INTO shared_info
rdcID =#{rdcID } , releaseID =#{releaseID } , codeLave1 =#{codeLave1 } , codeLave2 =#{codeLave2 } , codeLave3 =#{codeLave3 } , codeLave4 =#{codeLave4 } , title =#{title } , typeCode =#{typeCode } , typeText =#{typeText } , unitPrice =#{unitPrice } , sqm =#{sqm } , unit =#{unit } , unit1 =#{unit1 } , unit2 =#{unit2 } , validStartTime =#{validStartTime } , validEndTime =#{validEndTime } , applyID =#{applyID } , bookings =#{bookings } , attrvalue =#{attrvalue } , attrvalue1 =#{attrvalue1 } , attrvalue2 =#{attrvalue2 } , telephone =#{telephone } , note =#{note } , stauts =#{stauts } , stprovinceID =#{stprovinceID } , stcityID =#{stcityID } , staddress =#{staddress } , toprovinceID =#{toprovinceID } , tocityID =#{tocityID } , toaddress =#{toaddress } , provinceid =#{provinceid } , cityid =#{cityid } , address =#{address } , detlAddress =#{detlAddress } , dataType =#{dataType }
